con:testSuite.con:tearDownScript:
for (testCaseResult in runner.results){
	testCaseName = testCaseResult.getTestCase().name

	for (testStepResult in testCaseResult.getResults()) {
		log.info("testing")
		log.info("response: " + testStepResult.getTestStep().testRequest.getResponse().getContentAsString())	
	}
//		testStepResult.messages.each() { msg -> log.info msg }
}

testStepDoc = ""

for (testCase in testSuite.getTestCaseList()) {
	for (testStep in testCase.getTestStepList()) {


	testStepDoc += "Test Name:\t" + testStep.name
	testStepDoc += "URL Called:\t" + testStep.resource
	testStepDoc += "Type:\t" + testStep.restMethod.name.toUpperCase()
	testStepDoc += "Params:"
	for (param in testStep.getPropertyValue("params").tokenize(", ")) {
		testStepDoc += param + ":\t" + testStep.getPropertyValue(param)
	}

	if (testStep.restMethod.name == "post") {
		testStepDoc += "Passed Data:"
	}
	testStepDoc += "Expected Response:\t" + testStep.getPropertyValue("expResp")
	testStepDoc += "Coverage:\n" + testStep.getPropertyValue("purpose")
	testStepDoc += "Pass/Fail:\t"

	testStepDoc += "Assertions:\n"
	for (assertion in testStep.getAssertionList()) {
		testStepDoc += assertion.getToken()
		testStepDoc += assertion.DESCRIPTION
		testStepDoc += assertion.ID
		testStepDoc += assertion.LABEL
		testStepDoc += assertion.toString()
	}
	}
}

log.info "testStepDoc"
log.info testStepDoc

new File("./soap_test_Doc").write(testStepDoc)
docFile.write(testStepDoc)

log.info "done"

